nations = dlmread("nations.csv", ",", 1, 0);population = nations(:,7);gdp_millions = nations(:,4) * 1000000;life_expectancy = nations(:,5);bip = gdp_millions ./ population;#Endvariablenbest_pol = [];best_abs = 100;best_coeffs = [];best_rmse = 1000;function result = polynom (x, k, rang, abs)  #Berechnung nach Folie, x ist Wertevektor, k koeffizienten  polynom_mask = bsxfun(@power, x, (0:rang));  result = sum(polynom_mask .* k, 2) + abs;    return;endfunctionfunction result = rmse(orig_data, prediction)    result = sqrt(sum((prediction .- orig_data) .^ 2) / size(prediction,1));    return;endfunction#10k Durchläufefor i=1:10000  #Random Koeffizienten plus absoluten Wert  rand_coeffs = unifrnd(-0.00005, 0.0001, [1,3]);  abs = randi([65, 70]);  pol = polynom(bip, rand_coeffs, 2, abs);  error = rmse(life_expectancy, pol);    #rmse von Durchlauf wird berechnet und bei besserem Wert abgespeichert  if error < best_rmse    best_rmse = error;    best_coeffs = rand_coeffs;    best_pol = pol;    best_abs = abs;  endif    endforscatter(bip, life_expectancy, "r", "x");hold;plot(bip, best_pol, "xb");